import React, {useEffect, useState} from "react"; 
import {Container, AppBar, Typography, Grow, Grid, ThemeProvider, useTheme} from '@mui/material';//need to fit new package version
import { useDispatch } from "react-redux";

import {getPosts} from './actions/posts';
import Posts from "./components/Posts/Posts";
import Form from "./components/Form/Form";
import Navbar from "./components/Navbar/Navbar";
//import useStyles from './styles';
import { StyledAppBar, styles } from "./styles";

const App = () => {
    //const classes = useStyles();
    const theme = useTheme();
    const dispatch = useDispatch();
    const [currentId, setCurrentId] = useState(0);

    useEffect( () => {
        dispatch(getPosts());
    }, [currentId, dispatch]);

    return (
        <Container maxWidth='lg'>
            <Navbar />
            <Grow in>
                <Container>
                    <ThemeProvider theme={theme}>
                        <Grid style={styles.mainContainer} container justify="space-between" alignItems={"stretch"} spacing={3}>
                            <Grid item xs={12} sm={7}>
                                <Posts setCurrentId={setCurrentId} />
                            </Grid>
                            <Grid item xs={12} sm={4}>
                                <Form currentId={currentId} setCurrentId={setCurrentId} />
                            </Grid>
                        </Grid>
                    </ThemeProvider>
                </Container>
            </Grow>
        </Container>
    );
}

export default App;
