import React, {useEffect} from "react"; 
import {Container, AppBar, Typography, Grow, Grid} from '@mui/material';//need to fit new package version
import { useDispatch } from "react-redux";

import {getPosts} from './actions/posts';
import Posts from "./components/Posts/Posts";
import Form from "./components/Form/Form";
import memories from './images/memories.jpeg';
//import useStyles from './styles';
import { styledAppBar, styles } from "./styles";

const App = () => {
    //const classes = useStyles();
    const dispatch = useDispatch();

    useEffect( () => {
        dispatch(getPosts());
    }, [dispatch])

    return (
        <Container maxWidth='lg'>
            <styledAppBar position="static" color="inherit">
                <Typography variant="h2" align="center">Memories</Typography>
                <img src={memories} alt="memories" height="60" style={styles.image}/>
            </styledAppBar>
            <Grow in>
                <Container>
                    <Grid container justify="space-between" alignItems={"stretch"} spacing={5}>
                        <Grid item xs={12} sm={7}>
                            <Posts />
                        </Grid>
                        <Grid item xs={12} sm={7}>
                            <Form />
                        </Grid>
                    </Grid>
                </Container>
            </Grow>
        </Container>
    );
}

export default App;
